import * as echarts from "echarts";
export default {
  name: 'Home',
  data() {
    return {};
  },
  mounted() {
    this.GetIntegralConsumeAndGainChart();
  },
  methods: {
    // 统计最近30天积分的消耗和获取的折线图
    async GetIntegralConsumeAndGainChart() {
      try {
        let {
          Data
        } = await this.$Post('/Integral/GetIntegralConsumeAndGainChart', this.searchForm);

        // 在 DOM 更新后初始化 ECharts 图表
        this.$nextTick(() => {
          let myChart = echarts.init(document.getElementById(`echartDiv`));
          var option = {
            title: {
              text: '最近30天的积分的获取和使用'
            },
            tooltip: {
              trigger: 'axis'
            },
            legend: {},
            toolbox: {
              show: true,
              feature: {
                dataZoom: {
                  yAxisIndex: 'none'
                },
                dataView: {
                  readOnly: false
                },
                magicType: {
                  type: ['line', 'bar']
                },
                restore: {},
                saveAsImage: {}
              }
            },
            xAxis: {
              type: 'category',
              boundaryGap: false,
              data: Data.map(x => x.Date)
            },
            yAxis: {
              type: 'value',
              axisLabel: {
                formatter: '{value} 积分'
              }
            },
            series: [{
              name: '获取',
              type: 'line',
              data: Data.map(x => x.gainIntegral),
              markPoint: {
                data: [{
                  type: 'max',
                  name: 'Max'
                }, {
                  type: 'min',
                  name: 'Min'
                }]
              },
              markLine: {
                data: [{
                  type: 'average',
                  name: 'Avg'
                }]
              }
            }, {
              name: 'Lowest',
              type: 'line',
              data: [1, -2, 2, 5, 3, 2, 0],
              markPoint: {
                data: [{
                  name: '周最低',
                  value: -2,
                  xAxis: 1,
                  yAxis: -1.5
                }]
              },
              markLine: {
                data: [{
                  type: 'average',
                  name: 'Avg'
                }, [{
                  symbol: 'none',
                  x: '90%',
                  yAxis: 'max'
                }, {
                  symbol: 'circle',
                  label: {
                    position: 'start',
                    formatter: 'Max'
                  },
                  type: 'max',
                  name: '最高点'
                }]]
              }
            }]
          };
          myChart.setOption(option); // 渲染图表
          // 随着屏幕大小调节图表
          window.addEventListener("resize", () => {
            myChart.resize();
          });
        });
      } catch (error) {
        console.error("获取数据失败:", error);
      }
    }
  }
};
